const Config = require("../config.json");

class API {
  url = Config.BASE_URL;

  async getServerStatus() {
    const status = await fetch(`${this.url}/health`)
      .then(resp => resp.json());
    return status;
  }

  async getUrls() {
    const urls = await fetch(`${this.url}/urls`)
      .then(resp => resp.json());

    return urls;
  }
}

export default new API();
