import React from "react";
import Layout from "../components/Layout";
import Api from "../utils/api";
import { Alert, Table, TableHeader, TableBody } from "@patternfly/react-core";

export default class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      urls: []
    }
  }

  async componentDidMount() {
    let urls = await Api.getUrls();
    this.setState({ urls });
  }

  render() {
    const { urls } = this.state;
    const columns = ["Short Url", "Redirects To", "Link"];
    const rows = urls.map(u => {
      return [
        u.route, u.url, "http://link"
      ];
    });
    return (
      <Layout>
        {urls.length === 0 && 
          <Alert variant="warning" isInline title="No routes defined.">
            You can use the "Add New" link in the top navigation to add your first route."
          </Alert>
        }{rows}
      </Layout>
    )
  }
}
