import { PageSection, Title } from "@patternfly/react-core";
import React from "react";
import Layout from "../components/Layout";
import Config from "../config.json";

export default class About extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      serverUp: false
    }
  }

  componentDidMount() {
    fetch(`${Config.BASE_URL}/status`)
      .then(resp => resp.json())
      .then(data => {
        if (data) {
          this.setState({serverUp: true})
        }
      })
      .catch(e => {
        console.log(e);
        this.setState({serverUp: false})
      });
  }

  render() {
    return (
      <Layout>
        <PageSection>
          <Title headingLevel="h1">Server Status</Title>

          <p>
            Server is currently
            { this.state.serverUp && 
              <span>Up and running</span>
            }
            { !this.state.sererUp}
          </p>
        </PageSection>
      </Layout>
    )
  }
}
