import { Alert, PageSection, Title } from "@patternfly/react-core";
import React from "react";
import Layout from "../components/Layout";
import Api from "../utils/api";

export default class About extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      status: {
        server: false,
        database: false
      }
    }
  }

  async componentDidMount() {
    Api.getServerStatus()
      .then(status => {
        if (status) {
          this.setState({status})
        }
      })
      .catch(e => {
        console.log(e);
        this.setState({status: {server: false, database: false}})
      });
  }

  render() {
    const { status } = this.state;
    return (
      <Layout>
        <PageSection>
          <Title headingLevel="h1">Server Status</Title>
        </PageSection>
        <PageSection>
          <Alert variant={status.server ? "success" : "danger"} isInline title="Server Status">
          { status.server && 
            <React.Fragment>Up and running</React.Fragment>
          }
          { !status.server && 
            <span>Unreachable</span>
          }
          </Alert>
          Server is currently:{" "}

          Database status: {" "}
          { status.database && 
            <span>Up and running</span>
          }
          { !status.database && 
            <span>Unreachable</span>
          }
        </PageSection>
      </Layout>
    )
  }
}
